mapper.select:
SELECT A.PK_PROD_SEQ AS pkProdSeq , A.PROD_NAME AS prodName , A.PROD_TYPE AS prodType , A.PROD_KEYWORD AS prodKeyword , A.PROD_PRICE AS prodPrice , A.PROD_IMG AS prodImg , A.REGIST_ID AS registId , A.REGIST_DT AS registDt FROM product A WHERE 1=1
AND PROD_NAME LIKE CONCAT('%', #{keyword}, '%')
AND PROD_TYPE = #{prodType}
ORDER BY A.PK_PROD_SEQ ASC LIMIT #{pageStart}, #{perPageNum}

SELECT A.PK_RESULT_DETAIL_SEQ AS pkResultDetailSeq , A.PK_PROD_SEQ AS pkProdSeq , A.PK_RESULT_SEQ AS pkResultSeq , A.PK_USER_SEQ AS pkUserSeq , B.PROD_NAME AS prodName , B.PROD_TYPE AS prodType , B.PROD_KEYWORD AS prodKeyword , B.PROD_PRICE AS prodPrice , B.PROD_IMG AS prodImg , B.REGIST_ID AS registId , DATE_FORMAT(A.REGIST_DT,'%Y-%m-%d') AS registDt FROM result_detail A INNER JOIN product B ON A.PK_PROD_SEQ = B.PK_PROD_SEQ WHERE 1=1 AND A.PK_USER_SEQ = #{pkUserSeq}
AND PROD_NAME LIKE CONCAT('%', #{keyword}, '%')
AND PROD_TYPE = #{prodType}
ORDER BY A.PK_PROD_SEQ ASC LIMIT #{pageStart}, #{perPageNum}

SELECT COUNT(*) FROM result_detail A INNER JOIN product B ON A.PK_PROD_SEQ = B.PK_PROD_SEQ WHERE 1=1 AND A.PK_USER_SEQ = #{pkUserSeq}
AND PROD_NAME LIKE CONCAT('%', #{keyword}, '%')
AND PROD_TYPE = #{prodType}
ORDER BY A.PK_PROD_SEQ ASC
