mapper.select:
SELECT A.PK_SUBJECT_SEQ AS pkSubjectSeq, A.PK_EDUCATION_SEQ AS pkEducationSeq, (SELECT SB.EDUCATION_NAME FROM EDUCATION SB WHERE SB.PK_EDUCATION_SEQ = #{pkEducationSeq}) AS educationName, A.PK_MANAGER_SEQ AS pkManagerSeq, B.NAME AS name, A.SUBJECT_NAME AS subjectName, CASE A.SUBJECT_STATE WHEN 'A' THEN '준비' WHEN 'B' THEN '진행' WHEN 'C' THEN '완료' ELSE A.SUBJECT_STATE END AS subjectState, CONCAT(DATE_FORMAT(A.SUB_START_DT, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(A.SUB_END_DT, '%Y-%m-%d')) AS subjectDt, DATE_FORMAT(A.SUB_START_DT, '%Y-%m-%d') AS subStartDt, DATE_FORMAT(A.SUB_END_DT, '%Y-%m-%d') AS subEndDt, A.REGIST_ID AS registId, DATE_FORMAT(A.REGIST_DT, '%Y-%m-%d') AS registDt, A.UPDATE_ID AS updateId, DATE_FORMAT(A.UPDATE_DT, '%Y-%m-%d') AS updateDt, (SELECT GROUP_CONCAT(PK_USER_SEQ SEPARATOR ',') FROM EDUCATION_STUD SA WHERE SA.PK_EDUCATION_SEQ = #{pkEducationSeq}) AS pkUserSeqArray FROM SUBJECT A INNER JOIN MANAGER B ON A.PK_MANAGER_SEQ = B.PK_MANAGER_SEQ WHERE 1=1
AND A.PK_EDUCATION_SEQ = #{pkEducationSeq}
AND A.PK_MANAGER_SEQ = #{pkManagerSeq}
AND A.PK_EDUCATION_SEQ IN (SELECT WA.PK_EDUCATION_SEQ FROM EDUCATION_STUD WA WHERE WA.PK_USER_SEQ IN (#{pkUserSeq}))
ORDER BY A.PK_SUBJECT_SEQ ASC
