mapper.select:
SELECT A.PK_EXAM_SEQ AS pkExamSeq , A.PK_EXAM_DETAIL_SEQ AS pkExamDetailSeq , A.PK_SUBJECT_SEQ AS pkSubjectSeq , (SELECT SB.SUBJECT_NAME FROM SUBJECT SB WHERE SB.PK_SUBJECT_SEQ = A.PK_SUBJECT_SEQ) AS subjectName , A.PK_EXAM_RESULT_SEQ AS pkExamResultSeq , A.PK_USER_SEQ AS pkUserSeq , (SELECT SA.NAME FROM USER SA WHERE A.PK_USER_SEQ = SA.PK_USER_SEQ) AS name , A.REGIST_ID AS registId , A.REGIST_DT AS registDt , A.UPDATE_ID AS updateId , A.UPDATE_DT AS updateDt FROM LMS_EXAM_RESULT A INNER JOIN LMS_EXAM B ON A.PK_EXAM_SEQ= B.PK_EXAM_SEQ WHERE 1=1
AND A.PK_USER_SEQ = #{pkUserSeq}
GROUP BY A.PK_USER_SEQ

mapper.insert:
	    INSERT INTO LMS_EXAM_RESULT
  			(
  			PK_EXAM_SEQ
  			, PK_EXAM_DETAIL_SEQ
  			, PK_SUBJECT_SEQ
  			, PK_USER_SEQ
  			, ANSWER
  			, REGIST_ID
  			, REGIST_DT
  			)
  		VALUES
  			(
  			#{pkExamSeq}
  			, #{pkExamDetailSeq}
  			, #{pkSubjectSeq}
  			, #{pkUserSeq}
  			, #{answer}
  			, #{registId}
  			, SYSDATE()
  			)

mapper.update:
UPDATE LMS_EXAM
SET EXAM_DIVN = 'Y'
WHERE PK_EXAM_SEQ = #{pkExamSeq}

UPDATE LMS_EXAM_RESULT
SET 
RESULT_DIVN = 'Y'
, ANS_RESULT = #{ansResult}
WHERE PK_EXAM_RESULT_SEQ = #{pkExamResultSeq}
